import { View, Text, StyleSheet, Image } from 'react-native'
import React from 'react'
import { Color } from '../GlobalStyles'

const CoinItem = ({ coin }) => {
  return (
    <View style={styles.containerItem}>
      <View style={styles.coinName}>
      <Image
        style={styles.image}
        source={{uri: coin.image}}
      />
      <View style={styles.containerNames}>
      <Text style={styles.text}>{coin.name}</Text>
      <Text style={styles.textSymbol}>{coin.symbol}</Text>
      </View>
      </View>
      <View>
      <Text style={styles.textPrice}> { coin.current_price ? coin.current_price.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 }) : '0.00'} </Text>
      <Text style={[styles.pricePercentage, coin.price_change_percentage_24h > 0 ? 
            styles.priceUp : styles.priceDown]}>
      {coin.price_change_percentage_24h}
      </Text>
      </View>
    </View>
  )
}

export default CoinItem;

const styles = StyleSheet.create({
  containerItem: {
    backgroundColor: Color.blackModePrimaryDark,
    paddingTop: 10,
    flexDirection: 'row',
    justifyContent: 'space-between'
  },
  textSymbol:{
    color: Color.gray_400,
    textTransform: 'uppercase',


  },
  containerNames:{
    marginLeft: 10,

  },
  text: {
    color: 'white',
  },
  textPrice: {
    color: 'white',
    textAlign: 'right'
    
  },
  image: {
    width: 30,
    height: 30
  },
  coinName:{
    flexDirection: 'row',

  },
  pricePercentage: {
    textAlign: 'right',

  },
  priceUp: {
    color: '#00B5B9'
  },
  priceDown: {
    color: '#fc4422'
  }


});
