import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, ActivityIndicator, StyleSheet, Alert } from 'react-native';
import { firebase } from '../firebase';

const Logs = () => {
  const [logInfo, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const todoRef = firebase
      .firestore()
      .collection('users')
      .doc('LlsVwIQz9hMMDtHgQLlT')
      .collection('Logs');

    const unsubscribe = todoRef.onSnapshot((querySnapshot) => {
      const logs = [];
      querySnapshot.forEach((doc) => {
        const { ReceiverUid, Timestamp, transactions } = doc.data();
        const formattedTimestamp = Timestamp.toDate().toLocaleString();
        logs.push({
          id: doc.id,
          ReceiverUid,
          Timestamp: formattedTimestamp,
          transactions,
        });
      });
      setLogs(logs);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const renderItem = ({ item }) => {
    const isRecent = isRecentTransaction(item);
    return (
      <View style={[styles.logItem, isRecent && styles.recentLogItem]}>
        <Text style={[styles.logText, isRecent && styles.recentLogText]}>
          Sent: ${item.transactions} To {item.ReceiverUid} in {item.Timestamp}
        </Text>
      </View>
    );
  };

  const isRecentTransaction = (item) => {
    const currentTime = new Date();
    const logTime = new Date(item.Timestamp);
    const diffInSeconds = Math.abs((currentTime - logTime) / 1000);
    const RECENT_THRESHOLD = 300; // 5 minutes (adjust as needed)

    return diffInSeconds <= RECENT_THRESHOLD;
  };

  return (
    <View style={styles.container}>
      {loading ? (
        <ActivityIndicator size="large" color="#0000ff" />
      ) : (
        <FlatList
          data={logInfo}
          keyExtractor={(item) => item.id}
          renderItem={renderItem}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: 16,
  },
  logItem: {
    marginBottom: 10,
    padding: 10,
    backgroundColor: '#f0f0f0',
    borderRadius: 5,
  },
  recentLogItem: {
    backgroundColor: '#e4f0ff', // Light blue color for recent transactions
  },
  logText: {
    fontSize: 16,
  },
  recentLogText: {
    fontWeight: 'bold', // Bold text for recent transactions
  },
});

export default Logs;
