import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  TextInput,
  ImageBackground,
} from "react-native";
import {
  collection,
  runTransaction,
  doc,
  getDoc,
  addDoc,
} from "firebase/firestore";
import { auth, db, firebase } from "../firebase";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { Ionicons } from "@expo/vector-icons";

const Dashboard = ({ route, navigation }) => {
  const [balance, setBalance] = useState(5000); // Initial balance
  const [email, setEmail] = useState();
  const [uids, setUid] = useState();
  const [userInfo, setUserInfo] = useState([]);
  const [transactions, setTransactions] = useState();
  const [fullname, setName] = useState();
  const [uid2, setUid2] = useState();
  const [amount, setAmount] = useState();

  const transferFunds = async () => {
    const sfDocRef = doc(db, "users", uid2);
    try {
      await runTransaction(db, async (transaction) => {
        const sfDoc = await transaction.get(sfDocRef);
        if (!sfDoc.exists()) {
          throw "Document does not exist!";
        }
        const newWallet = sfDoc.data().wallet + Number(amount);
        transaction.update(sfDocRef, { wallet: newWallet });

        const userRef = doc(db, "users", auth.currentUser.uid);
        const userSnapshot = await transaction.get(userRef);
        if (!userSnapshot.exists()) {
          throw "User document does not exist!";
        }
        const deductedWallet = userSnapshot.data().wallet - Number(amount);
        transaction.update(userRef, { wallet: deductedWallet });

        setBalance(deductedWallet); // Update the balance state

        const logRef = collection(db, "users", auth.currentUser.uid, "Logs");
        await addDoc(logRef, {
          transactions: amount,
          Timestamp: new Date(),
          ReceiverUid: uid2,
        });

        alert(
          `You just sent $${Number(amount)} to ${uid2}. Your new balance is $${deductedWallet}.`
        );
        console.log(
          `You just sent $${Number(amount)} to ${uid2}. Your new balance is $${deductedWallet}.`
        );
      });
    } catch (e) {
      console.log("Transaction failed: ", e);
    }
  };
  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      if (user) {
        const uid = user.uid;
        setUid(uid);
        setEmail(user.email);

        const getWallet = async () => {
          const docRef = doc(db, "users", uid);
          const docSnap = await getDoc(docRef);
          if (docSnap.exists()) {
            console.log("Document data:", docSnap.data());
            const data = docSnap.data();
            setUserInfo(data);
            setBalance(data.wallet); // Update the balance state with the current wallet value
          } else {
            console.log("No such document!");
          }
        };
        getWallet();
      } else {
        navigation.navigate("Login");
      }
    });
  }, []);

  return (
    <View style={{ flex: 1, justifyContent: "center" }}>
      <ImageBackground source={require('../assets/background.jpg')} 
      resizeMode="cover" 
      style={styles.image}>
      
      <View style={styles.balanceContainer}>
        <Text style={styles.balanceText}>Current Balance</Text>
        <View style={styles.currentBalanceContainer}>
          <Text style={styles.amountText}>$ {balance}</Text> {/* Update to use the balance state */}
        </View>
      </View>
      <TextInput
        style={styles.input}
        placeholder="Input Reciever's UID: "
        value={uid2}
        onChangeText={setUid2}
      />
      <TextInput
        style={styles.input}
        placeholder="Input Amount: "
        value={amount}
        onChangeText={setAmount}
      />

      <TouchableOpacity style={styles.transferButton} onPress={transferFunds}>
        <Text style={styles.transferButtonText}>Send Funds</Text>
      </TouchableOpacity>
      </ImageBackground>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  balanceContainer: {
    marginBottom: 20,
    alignItems: "center",
    justifyContent: 'center'
  },
  input: {
    height: 40,
    borderColor: "black",
    borderWidth: 2,
    borderRadius: 5,
    marginBottom: 10,
    paddingHorizontal: 15,
    margin: 10,
    fontFamily: 'Arial',
  },
  image:{
    flex: 1,
    justifyContent: 'center'
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  currentBalanceContainer:{
    flexDirection: 'row',
    justifyContent: 'center',
  },
  reloadButton:{
    padding: 5,
    marginHorizontal: 10
  },
  balanceText: {
    margin:10,
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 10,
    
  },
  amountText: {
    fontSize: 18,
  },
  transferButton: {
    marginHorizontal: 80,
    backgroundColor: "black",
    paddingVertical: 10,
    borderRadius: 5,
    marginTop: 15,
  },
  transferButtonText: {
    color: "white",
    fontSize: 16,
    fontWeight: "bold",
    textAlign: "center",
  },
});

export default Dashboard;
