import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity, ImageBackground, Modal, Platform } from 'react-native';
import { useNavigation } from '@react-navigation/core';
import { auth, firebase } from '../firebase';
import { onSnapshot, orderBy } from 'firebase/firestore';
import { Color, FontFamily } from '../GlobalStyles';

import * as NotificationsExpo from 'expo-notifications';
import * as Permissions from 'expo-permissions';

const Notifications = () => {
  const [logInfo, setLogs] = useState([]);
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const navigation = useNavigation();

  const registerForPushNotifications = async () => {
    const { status } = await Permissions.askAsync(Permissions.NOTIFICATIONS);
    if (status !== 'granted') {
      console.log('Permission not granted!');
      return;
    }

    const token = await NotificationsExpo.getExpoPushTokenAsync();
    console.log('Notification token:', token);
    // Save the token to your backend or use it to send notifications
  };

  const listenForNotifications = () => {
    NotificationsExpo.addNotificationReceivedListener((notification) => {
      console.log('Received notification:', notification);
      // Handle the received notification
    });
  };

  const onPress = () => {
    navigation.navigate('Main');
  };

  const openModal = (transaction) => {
    setSelectedTransaction(transaction);
    setModalVisible(true);
  };

  const closeModal = () => {
    setSelectedTransaction(null);
    setModalVisible(false);
  };

  useEffect(() => {
    const user = firebase.auth().currentUser;
    if (user) {
      const uid = user.uid;
      const receivedMessagesRef = firebase
        .firestore()
        .collection('users')
        .doc(uid)
        .collection('notes')
        .doc('messages')
        .collection('receivedMessages');

      const unsubscribe = receivedMessagesRef.onSnapshot((snapshot) => {
        snapshot.docChanges().forEach((change) => {
          if (change.type === 'added') {
            const { transactions, SenderEmail, Note } = change.doc.data();
            // Trigger a notification when a new message is added
            sendNotification(transactions, SenderEmail, Note);
          }
        });
      });

      return () => unsubscribe();
    }
  }, []);

  const sendNotification = (transactions, senderEmail, note) => {
    const localNotification = {
      title: 'New Message',
      body: `You just received ₱${transactions} from ${senderEmail}${note ? ` with a note: ${note}` : ''}`,
      android: {
        sound: true,
      },
      ios: {
        sound: true,
      },
    };

    NotificationsExpo.presentNotificationAsync(localNotification);
  };

  useEffect(() => {
    registerForPushNotifications();
    listenForNotifications();
  }, []);

  return (
    <View style={styles.container}>
      <FlatList
        showsVerticalScrollIndicator={false}
        style={styles.flatlistContainer}
        data={logInfo}
        renderItem={({ item, index }) => (
          <TouchableOpacity
            style={[styles.logItem, index === 0 && styles.highlightedLog]}
            onPress={() => openModal(item)}
          >
            <Text style={styles.pesoText}>Received from {item.SenderEmail}</Text>
            <Text style={styles.pesoMoney}>+₱{item.transactions}</Text>
            <Text style={styles.timestampText}>{item.Timestamp}</Text>
          </TouchableOpacity>
        )}
        keyExtractor={(item) => item.id}
        contentContainerStyle={styles.listContainer}
      />
      <View style={styles.receivedButton}>
        <TouchableOpacity style={styles.buttonContainer} onPress={onPress}>
          <Text style={styles.buttonText}>Go Back Home</Text>
        </TouchableOpacity>
      </View>
      <Modal visible={modalVisible} animationType="slide" transparent={true} onRequestClose={closeModal}>
        <View style={styles.modalContainer}>
          {selectedTransaction && (
            <View style={styles.transactionModal}>
              <Text style={styles.modalText}>Transaction Details</Text>
              <Text style={styles.modTrans}>Transaction: ₱{selectedTransaction.transactions}</Text>
              <Text style={styles.modTrans}>Sender: {selectedTransaction.SenderEmail}</Text>
              <Text style={styles.modTrans}>Time: {selectedTransaction.Timestamp}</Text>
              <Text style={styles.modTrans}>Note: {selectedTransaction.Note}</Text>
              <TouchableOpacity style={styles.closeButton} onPress={closeModal}>
                <Text style={styles.closeButtonText}>Close</Text>
              </TouchableOpacity>
            </View>
          )}
        </View>
      </Modal>
    </View>
  );
};

export default Notifications;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'flex-start',
    backgroundColor: Color.blackModePrimaryDark,
  },
  buttonContainer: {
    marginHorizontal: 80,
    backgroundColor: '#7B61FF',
    paddingVertical: 10,
    borderRadius: 15,
    marginTop: 15,
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontFamily: FontFamily.poppinsMedium,
    textAlign: 'center',
  },
  receivedButton: {
    padding: 10,
    marginBottom: 10,
  },
  flatlistContainer: {
    padding: 20,
  },
  listContainer: {
    paddingBottom: 16,
  },
  logItem: {
    backgroundColor: 'rgba(255, 255, 255, 0.15)',
    padding: 16,
    marginBottom: 8,
    borderRadius: 8,
    elevation: 2,
  },
  highlightedLog: {
    backgroundColor: '#7B61FF',
    borderWidth: 2,
    borderColor: '#FFA500',
  },
  logText: {
    fontSize: 16,
    marginBottom: 8,
    fontWeight: 'bold',
  },
  pesoText: {
    color: 'white',
    fontFamily: FontFamily.poppinsMedium,
  },
  pesoMoney: {
    color: '#7CFC00',
    alignSelf: 'flex-end',
    fontSize: 20,
  },
  timestampText: {
    fontSize: 12,
    color: 'white',
    alignSelf: 'flex-end',
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  transactionModal: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 8,
    width: '80%',
  },
  modalText: {
    fontSize: 18,
    marginBottom: 10,
    fontFamily: FontFamily.poppinsBold,
  },
  closeButton: {
    marginTop: 20,
    backgroundColor: '#7B61FF',
    padding: 10,
    borderRadius: 8,
    alignSelf: 'flex-end',
  },
  closeButtonText: {
    color: 'white',
    fontFamily: FontFamily.poppinsMedium,
  },
  modTrans: {
    fontFamily: FontFamily.poppinsMedium,
    fontSize: 13,
  },
});
